<?phprequire_once($_SERVER['DOCUMENT_ROOT'].'/conf.php');//Renvoie true si le cookie existe et est rempli false sinonfunction existCookie($cookieName){	if(isset($_COOKIE[utf8_decode($cookieName)]) && $_COOKIE[utf8_decode($cookieName)] != ""){		return true;	}else{		return false;	}}//Crée un cookie si il n'existe pas et met à jour son délai si il existe déjàfunction createCookie($cookieName, $cookieValue){	setrawcookie($cookieName, $cookieValue, (time() + TIME_COOKIE));}//Supprime un cookie si il existefunction deleteCookie($cookieName){	if(existCookie($cookieName))		setrawcookie(utf8_decode($cookieName), "");}?>