<?phprequire_once($_SERVER['DOCUMENT_ROOT'].'/conf.php');class DataBase{	var $connexion;		function connect(){		// établissement de la connexion		$this->connexion = new mysqli(HOST,USER,PASSWORD,DATABASE);		if (mysqli_connect_error()) {			// échec de la connexion			die('Erreur de connexion (' . mysqli_connect_errno() . ') ' . mysqli_connect_error());		}		mysqli_set_charset($this->connexion,'utf8');		return $this->connexion;	}		function close(){		//Fermeture de la connexion au serveur MySQL		return mysqli_close($this->connexion);	}}?>