<?php//Ajout d'une ligne dans le fichier de logfunction addLog($tolog){	$logFile = fopen("logs/".date("dmY").".log", "a+");	fputs($logFile, date("d-m-Y H:i")." - ".$tolog."\r\n");	fclose($logFile);}//Ajout d'une ligne dans le fichier de logfunction addLogInscription($tolog){	$logInscription = fopen("logs/inscriptions/".date("m").".log", "a+");	fputs($logInscription, date("d-m-Y H:i")." - ".$tolog."\r\n");	fclose($logInscription);}?>