<?phprequire_once($_SERVER['DOCUMENT_ROOT']."/classes/functions.php");require_once($_SERVER['DOCUMENT_ROOT'].'/classes/database.php');class Mailing{	var $bd;		/*Constructeur*/	function Mailing(){		$bdd = new DataBase();		$this->bd = $bdd->connect();	}		//Retourne les infos du dernier mailing sauvegardé	function getLastMailing(){		$res = array();		$sql = 'SELECT * FROM `mailing` WHERE date_sauvegarde = (SELECT max(date_sauvegarde) FROM `mailing`)';		$result = mysqli_query($this->bd,$sql) or die(mysqli_error());		while($line = mysqli_fetch_array($result)) {			$res['date_sauvegarde'] = $line['date_sauvegarde'];			$res['titre'] = $line['titre'];			$res['contenu'] = $line['contenu'];		}		return $res;	}		//Sauvegarde d'un mailing	function saveMailing($titre, $contenu){		//Ajouts du favoris		$sql = 'INSERT INTO `mailing` (`date_sauvegarde`, `titre`, `contenu`) VALUES (null, '.qs($titre).', '.qs($contenu).');';		$res = mysqli_query($this->bd,$sql) or die(mysqli_error());		return $res;	}}?>