<?phpif(isset($_GET["tree"]) && isset($_GET["idUser"])){	require_once($_SERVER['DOCUMENT_ROOT'].'/classes/database.php');	$db = new DataBase();	$conn = $db->connect();	$tree = $_GET["tree"];	$idUser = $_GET["idUser"];		if($tree == "all"){		$res = array();		$res['identifier'] = 'ident';		$res['label'] = 'name';		$res['items'] = array();				$sql = 'SELECT * FROM `favoris` WHERE `idUser` = \''.$idUser.'\'';		$result = mysqli_query($conn,$sql) or die(mysqli_error());		$i=0;		while($line = mysqli_fetch_array($result)) {			$res2 = array();			$res2['ident'] = $i;//$line['adresse'];			$res2['name'] = $line['label'];			$res2['adresse'] = $line['adresse'];			array_push($res['items'], $res2);			$i++;		}				echo json_encode($res);	}}?>